<?php

namespace Busybee\StudentBundle\Repository;

use Busybee\InstituteBundle\Entity\Space;
use Busybee\InstituteBundle\Entity\Year;
use Busybee\StaffBundle\Entity\Staff;
use Doctrine\ORM\EntityRepository;

/**
 * ActivityRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ActivityRepository extends EntityRepository
{
    /**
     * @param Staff $staff
     * @param Year $year
     * @return array
     */
    public function findByTutor(Staff $staff, Year $year)
    {
        return $this->createQueryBuilder('a')
            ->leftJoin('a.tutor1', 't')
            ->leftJoin('a.tutor2', 'u')
            ->leftJoin('a.tutor3', 'v')
            ->leftJoin('a.year', 'y')
            ->where('y.id = :year_id')
            ->setParameter('year_id', $year->getId())
            ->andWhere('t.id = :tutor1_id')
            ->setParameter('tutor1_id', $staff->getId())
            ->orWhere('u.id = :tutor2_id')
            ->setParameter('tutor2_id', $staff->getId())
            ->orWhere('v.id = :tutor3_id')
            ->setParameter('tutor3_id', $staff->getId())
            ->getQuery()
            ->getResult();
    }

    /**
     * @param int $studentId
     * @param Year $year
     * @return array
     */
    public function findByStudent(int $studentId, Year $year)
    {
        return $this->createQueryBuilder('a')
            ->leftJoin('a.students', 's')
            ->leftJoin('a.year', 'y')
            ->where('y.id = :year_id')
            ->setParameter('year_id', $year->getId())
            ->andWhere('s.id = :student_id')
            ->setParameter('student_id', $studentId)
            ->getQuery()
            ->getResult();
    }

    /**
     * @param int $spaceId
     * @param Year $year
     * @return array
     */
    public function findBySpace(Space $space, Year $year)
    {
        return $this->createQueryBuilder('a')
            ->leftJoin('a.space', 's')
            ->leftJoin('a.year', 'y')
            ->where('y.id = :year_id')
            ->setParameter('year_id', $year->getId())
            ->andWhere('s.id = :space_id')
            ->setParameter('space_id', $space->getId())
            ->getQuery()
            ->getResult();
    }
}
